import React, { Component } from "react";
import "./App.css";
import Clarifai from "clarifai";
import Navigation from "./components/Navigation/Navigation";
import Logo from "./components/Logo/Logo";
import ImageLinkForm from "./components/ImageLinkForm/ImageLinkForm";
import FaceRecognition from "./components/FaceRecognition/FaceRecognition";
import Rank from "./components/Rank/Rank";
import Particles from "react-particles-js";

const app = new Clarifai.App({ apiKey: "b245f7327c72491f83ffec3cdbfffb85" });
const particlesOptions = {
  particles: {
    number: 30,
    density: {
      enable: true,
      value_area: 800,
    },
  },
};
// function App() {
//   return (
//     <div className="App">
//       <Particles className="particles" params={particlesOptions} />
//       <Navigation />
//       <Logo />
//       <Rank />
//       <ImageLinkForm />
//       {/* <FaceRecognition /> */}
//     </div>
//   );
// }

// 就我理解，需要使用到state这个属性必须要有constructor方法，即要通过class来声明
class App extends Component {
  constructor() {
    // 要使用this必须要先使用super
    super();
    // 设定state，可以让子组件和父组件进行数据互动，有点类似于闭包
    this.state = {
      urlInput: "",
      imageUrl: "",
    };
  }

  // 利用箭头函数解决this的指向问题
  // 设置这个方法是为了改变state里面属性的值，子组件不能直接修改state的值，只能调用父组件的方法，从而达到修改state的目的
  // 这里获得的event为调用这个方法的事件
  onInputChange = (event) => {
    console.log(event.target.value); //调用 事件对象.target.value可以获得输入input的值
    // 调用这个继承自Component的方法来进行设定属性，不要通过 this.state.urlInput来设置值
    // 传入参数为一个对象
    // 当点击按钮时改变State中imageUrl的值,这个值为输入的url的值
    this.setState({ imageUrl: this.state.urlInput });
  };
  onButtonSubmit = () => {
    console.log("click");
    // 当点击按钮的时候和clarifai的api做交流
    app.models
      .initModel({
        id: Clarifai.COLOR_MODEL,
        version: "aa7f35c01e0642fda5cf400f543e7c40",
      })
      .then((generalModel) => {
        console.log(generalModel);
        return generalModel.predict(
          "https://i.epochtimes.com/assets/uploads/2020/01/GettyImages-93243663-450x300.jpg"
        );
      })
      .then((response) => {
        console.log(response);
        var concepts = response["outputs"][0]["data"]["concepts"];
        console.log(concepts);
      });
  };

  render() {
    return (
      <div className="App">
        <Particles className="particles" params={particlesOptions} />
        <Navigation />
        <Logo />
        <Rank />
        {/* 传入属性值，这样子组件可以获取数据，而且可以调用这里的方法 */}
        {/* 第一个onInputChange为属性名，第二个为属性值，记得要加this，代表的是这个App类的方法 */}
        <ImageLinkForm
          onInputChange={this.onInputChange}
          onButtonSubmit={this.onButtonSubmit}
        />
        <FaceRecognition imageUrl={this.state.imageUrl} />
      </div>
    );
  }
}

export default App;
